    out_path = os.path.join(EXPORT_DIR, f"ar_pose_{uuid.uuid4()}.{fmt}")
    if fmt == "glb": bpy.ops.export_scene.gltf(filepath=out_path, export_format="GLB")
    print(f"[AR] Exported to {out_path}")
    return out_path

